parameters.style:
.animated {
  opacity: 0;
  transform: scale(0);
  animation: pop-in 0.75s forwards;
}

@keyframes pop-in {
  0% {
    opacity: 0;
    transform: scale(0);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

.fade {
  opacity: 0;
  background-color: transparent;  /* Start with a transparent background */
  animation: fade-in 0.5s forwards, background-fade-in 0.5s forwards;
}

@keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

@keyframes background-fade-in {
  0% {
    background-color: transparent;
  }
  100% {
    background-color: your-desired-color;  /* Replace with your desired background color */
  }
}

.slideup {
  opacity: 0;
  transform: translateY(2500px); /* Start with the element positioned below its normal position */
  animation: slide-up 1s forwards;
  animation-delay: 1500ms; /* Add a delay before the animation starts */
}

@keyframes slide-up {
  0% {
    opacity: 0;
    transform: translateY(100%); /* Start from below */
  }
  100% {
    opacity: 1;
    transform: translateY(0); /* End at its normal position */
  }
}

