const id = Inputs.ID || '';
const label = Inputs.Label || '';
const checked = Inputs.Checked || false;
const icon = Inputs.Icon || ''; // Added icon
const obtained = Inputs.Obtained || ''; // Added obtained
const price = Inputs.Price || ''; // Added price

// Function to get the current date in the desired format
function getCurrentDate() {
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
}

// Function to load data from localStorage and check the date
function loadSelection(id, callback) {
    const storedData = localStorage.getItem(id);
    if (storedData) {
        const parsedData = JSON.parse(storedData);
        const currentDate = getCurrentDate();
        if (parsedData.date !== currentDate) {
            // If the stored date is not today, delete the key from localStorage
            localStorage.removeItem(id);
            callback(null);
        } else {
            callback(parsedData);
        }
    } else {
        callback(null);
    }
}

// Function to handle passthrough
function passthrough() {
    Outputs.ID = id;
    Outputs.Label = label;
    Outputs.Checked = checked;
    Outputs.Icon = icon; // Added icon
    Outputs.Obtained = obtained; // Added obtained
    Outputs.Price = price; // Added price
    Outputs.Date = getCurrentDate(); // Added current date
    Outputs.Failure();
}

// Main logic
loadSelection(id, function(result) {
    if (result) {
        Outputs.ID = result.id;
        Outputs.Label = result.label;
        Outputs.Checked = result.checked;
        Outputs.Icon = result.icon || ''; // Added icon handling
        Outputs.Obtained = result.obtained || ''; // Added obtained handling
        Outputs.Price = result.price || ''; // Added price handling
        Outputs.Date = getCurrentDate(); // Added current date
        Outputs.Success();
    } else {
        passthrough();
    }
});

