const icon = Inputs.Icon || ''; // Added icon
const obtained = Inputs.Obtained || ''; // Added obtained
const price = Inputs.Price || ''; // Added price
const date = Inputs.Date || ''; // Added date

// Function to load data from localStorage
function loadSelection(id, callback) {
    const storedData = localStorage.getItem(id);
    if (storedData) {
        callback(JSON.parse(storedData));
    } else {
        callback(null);
    }
}

// Function to save data to localStorage
function saveSelection(selection) {
    localStorage.setItem(selection.id, JSON.stringify(selection));
}

// Function to delete data from localStorage
function deleteSelection(id) {
    localStorage.removeItem(id);
}

// Function to check if data has changed and save or delete if necessary
function checkAndUpdateSelection(selection) {
    loadSelection(selection.id, function(existingItem) {
        let dataChanged = false;

        if (existingItem) {
            if (selection.checked === false) {
                // If the checkbox is unchecked, delete the entry
                deleteSelection(selection.id);
                Outputs.SavedSelection = null; // Indicate that nothing was saved
                Outputs.Success();
            } else {
                // Check if the existing data is different from the new data
                if (existingItem.label !== selection.label ||
                    existingItem.checked !== selection.checked ||
                    existingItem.icon !== selection.icon ||
                    existingItem.obtained !== selection.obtained ||
                    existingItem.price !== selection.price ||
                    existingItem.date !== selection.date) { // Added date comparison
                    dataChanged = true;
                }

                if (dataChanged) {
                    saveSelection(selection);
                    Outputs.SavedSelection = selection;
                    Outputs.Success();
                } else {
                    passthrough();
                }
            }
        } else {
            if (selection.checked) {
                // If the checkbox is checked and no existing item, save the new item
                saveSelection(selection);
                Outputs.SavedSelection = selection;
                Outputs.Success();
            } else {
                passthrough();
            }
        }
        // Signal completion
        Outputs.Completed = true; // or you can use Outputs.Completed = new Date().getTime(); for a timestamp
