// Function to check if the stored date is today's date
const isToday = (dateString) => {
  const storedDate = new Date(dateString);
  const today = new Date();
  return storedDate.getFullYear() === today.getFullYear() &&
         storedDate.getMonth() === today.getMonth() &&
         storedDate.getDate() === today.getDate();
};

// Function to check if the stored date is valid
const isDateValid = (dateString) => {
  const storedDate = new Date(dateString);
  const today = new Date();
  return dateString !== null && storedDate <= today;
};

// Function to handle retrieval of values
const getOutputValue = (key) => {
  const storedData = JSON.parse(localStorage.getItem(key));
  const value = storedData?.value || false;
  const storedDate = storedData?.date || null;

  if (value && isDateValid(storedDate) && isToday(storedDate)) {
    return true;
  } else {
    // If the date is invalid or not today, clear the stored data
    localStorage.removeItem(key);
    return false;
  }
};

