// Define the default value for the number of repetitions
const repetitions = Inputs.Repetitions || 5;

// Define the default value for the delay between repetitions
const delay = Inputs.Delay || 1000;

// Define the default value for the signal to be repeated
const signal = Inputs.Signal || 'Signal';

// Check if the page is loaded
if (Inputs.PageLoaded) {
  // Repeat the signal for the specified number of repetitions
  for (let i = 0; i < repetitions; i++) {
    // Use setTimeout to delay the signal
    setTimeout(() => {
      // Trigger the signal
      Outputs[signal]();
    }, i * delay);
  }
  // Send the Success signal
  Outputs.Success();
} else {
  // If the page is not loaded, send the Failure signal
  Outputs.Failure();
}

