function checkLocalStorage() {
    // Check if the key 'nativefruit' exists in the local storage
    if(localStorage.getItem('nativefruit')) {
        // If it exists, get the value
        const nativeFruitValue = localStorage.getItem('nativefruit');

        // Assign the value to the output 'NativeFruit'
        Outputs.NativeFruit = nativeFruitValue;

        // Extract the fruit name from the URL path
        const fruitNameMatch = nativeFruitValue.match(/\/(\w+)_stamp\.png$/);
        if (fruitNameMatch && fruitNameMatch[1]) {
            // Capitalize the first letter of the fruit name
            const fruitName = fruitNameMatch[1];
            Outputs.NativeFruitText = fruitName.charAt(0).toUpperCase() + fruitName.slice(1);
        } else {
            // If the fruit name is not found in the value, set a default value
            Outputs.NativeFruitText = 'Fruit';
        }

        Outputs.Success();
    } else {
        // If 'nativefruit' doesn't exist, throw an error and call the 'Failure' signal
        Outputs.error = 'No nativefruit found in local storage';
        Outputs.NativeFruitText = 'Fruit';
        Outputs.Failure();
    }
}

// Call the function every 100ms
setInterval(checkLocalStorage, 100);

