const sqlite3 = require('sqlite3').verbose();
const db = new sqlite3.Database(':memory:');

const id = Inputs.ID || '';
const label = Inputs.Label || '';
const checked = Inputs.Checked || false;

db.serialize(function() {
  db.run("CREATE TABLE Objects (ID TEXT, Label TEXT, Checked BOOLEAN)");

  db.get('SELECT * FROM Objects WHERE ID = ?', [id], function(err, row) {
    if (err) {
      Outputs.error = err;
      Outputs.ID = id;
      Outputs.Label = label;
      Outputs.Checked = checked;
      Outputs.Failure();
    } else if (row) {
      Outputs.ID = row.ID;
      Outputs.Label = row.Label;
      Outputs.Checked = row.Checked;
      Outputs.Success();
    } else {
      Outputs.ID = id;
      Outputs.Label = label;
      Outputs.Checked = checked;
      Outputs.Failure();
    }
  });
});

db.close();

