    Outputs.Checked = checked;
    Outputs.Icon = icon; // Added icon
    Outputs.Failure();
    // Signal completion
    Outputs.Completed = true; // or you can use Outputs.Completed = new Date().getTime(); for a timestamp
}

// Main logic
const selection = { id, label, checked, icon }; // Added icon
if (changed) {
    checkAndUpdateSelection(selection);
} else {
    passthrough();
}

// Listen for the 'OnClick' signal
if (Inputs.OnClick) {
    // Check if the previous page URL includes "splash"
    if (document.referrer.includes("splash")) {
        // If it does, force navigation to the home page
        window.location.replace('/#/home');
    } else {
        // Otherwise, navigate back one page
        window.history.back();
    }

    // Signal that the operation was successful
    Outputs.Success();
} else {
    // If 'OnClick' signal is not received, signal a failure
    Outputs.Failure();
}

