// Function to format the time in 12-hour format
function formatAMPM(date) {
    let hours = date.getHours();
    let minutes = date.getMinutes();
    const ampm = hours >= 12 ? 'PM' : 'AM';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? '0' + minutes : minutes;
    const strTime = hours + ':' + minutes + ' ' + ampm;
    return strTime;
}

// Function to update the current time
function updateTime() {
    const date = new Date();
    const currentTime = formatAMPM(date);
    Outputs.CurrentTime = currentTime; // Assuming Outputs.CurrentTime is a valid reference
}

// Run updateTime immediately upon script execution
updateTime();

// Create an interval that updates the current time every second
setInterval(updateTime, 1000);

