// Define a function to get and count bug keys
function getAndCountBugKeys() {
    // Get all keys from local storage
    const keys = Object.keys(localStorage);

    // Filter keys that start with "bugs-"
    const bugsKeys = keys.filter(key => key.startsWith('bugs-'));

    // Count the number of bug keys
    const count = bugsKeys.length;

    // Output the count in the format "# / 15"
    Outputs.CountText = `Bugs Progress: ${count} / 40`;

    // Signal success
    Outputs.Success();
}

// Run the function once per second
setInterval(getAndCountBugKeys, 1000);

