const id = Inputs.ID || '';
const label = Inputs.Label || '';
const checked = Inputs.Checked || false;

// Open the database
const db = openDatabase('MyAppDatabase', '1.0', 'My App Database', 2 * 1024 * 1024);

// Check if the object exists in the database
db.transaction(function (tx) {
  tx.executeSql('SELECT * FROM Objects WHERE ID = ?', [id], function (tx, results) {
    if (results.rows.length > 0) {
      // If the object exists, send the existing data
      Outputs.ID = results.rows.item(0).ID;
      Outputs.Label = results.rows.item(0).Label;
      Outputs.Checked = results.rows.item(0).Checked;
      Outputs.Success();
    } else {
      // If the object does not exist, act as a passthrough
      Outputs.ID = id;
      Outputs.Label = label;
      Outputs.Checked = checked;
      Outputs.Failure();
    }
  }, function (tx, error) {
    // Handle any errors
    Outputs.error = error;
    Outputs.Failure();
  });
});

