const id = Inputs.ID || '';
const label = Inputs.Label || '';
const price = Inputs.Price || '';
const icon = Inputs.Icon || '';
const obtained = Inputs.Obtained || '';
const time = Inputs.Time || '';
const months = Inputs.Months || '';
const checked = Inputs.Checked || false; // Added checked

// Function to load data from localStorage
function loadSelection(id, callback) {
    const storedData = localStorage.getItem(id);
    if (storedData) {
        callback(JSON.parse(storedData));
    } else {
        callback(null);
    }
}

// Function to handle passthrough
function passthrough() {
    Outputs.ID = id;
    Outputs.Label = label;
    Outputs.Price = price;
    Outputs.Icon = icon;
    Outputs.Obtained = obtained;
    Outputs.Time = time;
    Outputs.Months = months;
    Outputs.Checked = checked; // Added checked
    Outputs.Failure();
}

// Main logic
loadSelection(id, function(result) {
    if (result) {
        Outputs.ID = result.id;
        Outputs.Label = result.label;
        Outputs.Price = result.price || '';
        Outputs.Icon = result.icon || '';
        Outputs.Obtained = result.obtained || '';
        Outputs.Time = result.time || '';
        Outputs.Months = result.months || '';
        Outputs.Checked = result.checked || false; // Added checked handling
        Outputs.Success();
    } else {
        passthrough();
    }
});

