      // If the object does not exist, act as a passthrough
      Outputs.ID = id;
      Outputs.Label = label;
      Outputs.Checked = checked;
      Outputs.Failure();
    }
  }, function (tx, error) {
    // Handle any errors
    Outputs.error = error;
    Outputs.Failure();
  });
});

