function getFishAndNookKeys() {
    let count = 0;

    // Iterate over all keys in local storage
    for (let i = 0; i < localStorage.length; i++) {
        // Check if the key starts with "fish-" or "nook-"
        const key = localStorage.key(i);
        if (key.startsWith('redd-') || key.startsWith('painting-')) {
            count++;
        }
    }

    // Output the count in the format "Nook Store Progress: # / 307"
    Outputs.CountText = `Redd Progress: ${count} / 71`;

    // Signal success
    Outputs.Success();
}

// Run the function every 100 milliseconds
setInterval(getFishAndNookKeys, 100);

