  tx.executeSql('SELECT * FROM Objects WHERE ID = ?', [id], function (tx, results) {
    if (results.rows.length > 0) {
      // If the object exists, send the existing data
      Outputs.ID = results.rows.item(0).ID;
      Outputs.Label = results.rows.item(0).Label;
      Outputs.Checked = results.rows.item(0).Checked;
      Outputs.Success();
