        if (localStorage.key(i).startsWith('stationery-')) {
            count++;
        }
    }

    // Output the count in the format "Fish Progress: # / 40"
    Outputs.CountText = `Stationery Progress: ${count} / 64`;

    // Signal success
    Outputs.Success();
}

// Run the function every 100 milliseconds
setInterval(getFishKeys, 100);

