function getFishKeys() {
    let count = 0;

    // Iterate over all keys in local storage
    for (let i = 0; i < localStorage.length; i++) {
        let key = localStorage.key(i);
        // Check if the key starts with "tools-" or "parasol-"
        if (key.startsWith('tools-') || key.startsWith('parasol-')) {
            count++;
        }
    }

    // Output the count in the format "Tool / Handheld Progress: # / 339"
    Outputs.CountText = `${count} / 64`;

    // Signal success
    Outputs.Success();
}

// Run the function every 100 milliseconds
setInterval(getFishKeys, 100);

