// Get all keys from local storage
const keys = Object.keys(localStorage);

// Define the prefixes
const prefixes = [
  'fossil-', 'bugs-', 'fish-', 'painting-',
  'clothing-', 'fruit-', 'tools-', 'gyroid-', 'tools-', 'songs-', 'stationery-',
  'animalisland-', 'christmas-', 'gulliver-', 
  'halloween-', 'misc-', 'nook-', 
  'raffle-', 'redd-', 'painting-', 
  'secret-', 'snowman-', 'summercamper-','thanksgiving-', 
  'tortimer-', 'igloo-'
];

// Filter keys that start with any of the prefixes
const matchingKeys = keys.filter(key => prefixes.some(prefix => key.startsWith(prefix)));

// Count the number of matching keys
const count = matchingKeys.length;

// Output the count in the format "# / 601"
Outputs.CountText = `${count} / 1237`;

// Signal success
Outputs.Success();

